import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [changePoint, setChangePoint] = useState(null);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/change-point')
      .then(res => setChangePoint(res.data))
      .catch(err => console.error(err));

    axios.get('http://localhost:5000/api/events')
      .then(res => setEvents(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <h2>Detected Change Point</h2>
      {changePoint ? (
        <p>{changePoint.date}</p>
      ) : (
        <p>Loading change point...</p>
      )}

      <h2>Events</h2>
      <ul>
        {events.map((event, i) => (
          <li key={i}>
            <strong>{event.Event}</strong> ({event.Category}) â€” {event.StartDate}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
